import React ,{Component} from 'react';

class Head extends Component{
  constructor(props){
    super(props);
    this.state={
      headerClassScroll:'',
      isActive: 'active'
    }
    this.handleClickMethod = this.handleClickMethod.bind(this);
  }
  handleClickMethod(){
    this.setState({ isActive:'active' });
  }
  componentDidMount(){
   window.addEventListener('scroll', () => {
           let activeClass = 'header-scrolled';
           if(window.scrollY === 0){
               activeClass = '';
           }
           this.setState({ headerClassScroll:activeClass });
        });
  }
	render()
	{
		return (
			<>
  <header id="header" className={"fixed-top d-flex justify-content-center align-items-center header-transparent " + this.state.headerClassScroll}>

    <nav id="navbar" className="navbar">
      <ul>
        <li><a className={"nav-link scrollto " + this.state.isActive} href="#hero" onClick="handleClickMethod()">Home</a></li>
        <li><a className={"nav-link scrollto " + this.state.isActive} href="#about" onClick="handleClickMethod()">About</a></li>
        <li><a className={"nav-link scrollto " + this.state.isActive} href="#resume" onClick="handleClickMethod()">Resume</a></li>
        <li><a className={"nav-link scrollto " + this.state.isActive} href="#portfolio" onClick="handleClickMethod()">Portfolio</a></li>
        
        <li><a className={"nav-link scrollto " + this.state.isActive} href="#contact">Contact</a></li>
      </ul>
      <i className="bi bi-list mobile-nav-toggle"></i>
    </nav>

  </header>
  
 </>
			);
	}
}
export default Head;
